
public class CheckPassFail {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int mark = 49;
		
		System.out.println("The mark is " + mark);
		
		if (mark >= 50){
			System.out.println("PASS");
		}else if(mark < 50){
			System.out.println("FAIL");
		}
		System.out.println("DONE");	
		}

	}

